import { GetInitialPropsContext, NextSFC2 } from '@@types/next'
import { Container, Header, TabBar } from 'components'
import Error from 'containers/Error'
import { ErrorObject } from 'interfaces/error'
import React from 'react'
import { connect } from 'react-redux'
import CreditTips from './CreditTips'
import InfoList from './InfoList'
import { getCreditEntrydata } from 'actions/myTrade/assess/list';

interface CreditEvaluationProps extends ErrorObject {
  assessList : any,
}

const CreditEvaluation: NextSFC2<CreditEvaluationProps> = ({ error ,assessList }: CreditEvaluationProps) => {
  if (error) {
    return <Error statusCode={error.status} errorMsg={error.errorMsg} />
  }
  const containerProps = {
    renderHeader: <Header>信用评估确认</Header>,
    renderTabBar: <TabBar hidden={true} />,
    bgColor:'#ffffff'
  }

  const creditData = [
    {
      credit_title: assessList.getIn('credit_title'),
      credit_desc : assessList.getIn('credit_desc'),
      credit_icon : assessList.getIn('credit_icon'),
    }
  ]

  return (
    <Container {...containerProps}>
        <CreditTips creditData={creditData}/>
        <InfoList />    
    </Container>
  )
}

CreditEvaluation.getInitialProps = async ({ store, query,  req }: GetInitialPropsContext) => {
    const data = await store.dispatch(getCreditEntrydata({ query, req }))
    if (data.status) {
      return { error: data }
    }
}

const mapStateToProps = (state: any) => ({
  assessList: state.getIn(['myTrade', 'assess', 'list']),
})

export default connect(mapStateToProps)(CreditEvaluation)
