import { Button, Flex } from 'antd-mobile'
import { Icon, Link, Switch } from 'components'
import React from 'react'
import styles from './InfoList.less'

const Item = Flex.Item

export interface InfoListProps {
  creditTips : string,
  submitText : string,
  submitStatus : string,
  agreementInfo : any,
  stepList : any
}

const InfoList: React.FunctionComponent<InfoListProps> = ({stepList, creditTips , submitText , submitStatus , agreementInfo }) => {
  console.log(stepList.toJS())
  return (
    <div className={styles.infoList}>
      <p className={styles.creditTips}>{ creditTips }</p>
      <div className={styles.list}>
        {
          stepList.toJS().map((item,key) => {
          return  <Flex key={key} className={styles.item} >
                    <Item className={styles.left}>
                      <img src={item.step_icon} alt="" />
                      { item.step_title }
                    </Item>
                    <Icon type={require('svg/arrow-right.svg')} size="xxs" color="#00AA99" />
                    <span style = {{ color : '#00AA99' }} className={styles.status}>{item.step_info}</span>
                  </Flex>
            })
        }

      </div>
      <Flex className={styles.agree}>
        <Switch
          className={styles.agreeText}
          checked={ true }
          onChange={ () => { console.log(0) }}
        >
          我已阅读并同意
        </Switch>
        <Link to={agreementInfo.agreementUrl} className={styles.agreement_name}>
          {agreementInfo.agreement_name}
        </Link>
      </Flex>
      <Button
        disabled={false}
        className={styles.submitText}
        type="warning"
        onClick={() => {

        }}
      >
        {submitText}
      </Button>
    </div>
  )
}

export default InfoList
