import { NextSFC2 } from '@@types/next'
import { Container, Header, TabBar } from 'components'
// import Error from 'components/Error'
import React from 'react'
import { connect } from 'react-redux'
// import { RState } from 'store'
import CreditTips from './CreditTips'
import InfoList from './InfoList'

export interface CreditEvaluationQuery {}

// const mapStateToProps = (state: RState) => ({
//   error: state.app.pageError,
//   //   select: state.select,
// })

// const mapDispatchToProps = (dispatch: RDispatch) => ({
// })


type ConnectedProps = ReturnType<typeof mapStateToProps>

interface CreditEvaluationProps extends ConnectedProps {
  query: CreditEvaluationQuery
}

const CreditEvaluation: NextSFC2<CreditEvaluationProps> = ({ error }: CreditEvaluationProps) => {
  if (error.code !== 200) {
    return <Error statusCode={error.code} status={error.status} errorMsg={error.errorMsg} />
  }

  const containerProps = {
    renderHeader: <Header>信用评估确认</Header>,
    renderTabBar: <TabBar hidden={true} />,
    bgColor:'#ffffff'
  }

  return (
    <Container {...containerProps}>
        <CreditTips />
        <InfoList />    
    </Container>
  )
}

// CreditEvaluation.getInitialProps = async ({}: GetInitialPropsContext<CreditEvaluationQuery>) => {

// }

export default CreditEvaluation
