import { Button, Flex, Toast, } from 'antd-mobile'
import { Link, Switch } from 'components'
import React from 'react'
import styles from './InfoList.less'

const Item = Flex.Item

export interface InfoListProps {
  creditTips: string
  submitText: string
  submitStatus: number
  agreementInfo: any
  stepList: any
}

const customIcon = () => (
  <div style={{padding:'0 28px'}}>
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 64 64" className="am-icon am-icon-md">
      <path fillRule="evenodd" d="M59.177 29.5s-1.25 0-1.25 2.5c0 14.47-11.786 26.244-26.253 26.244C17.206 58.244 5.417 46.47 5.417 32c0-13.837 11.414-25.29 25.005-26.26v6.252c0 .622-.318 1.635.198 1.985a1.88 1.88 0 0 0 1.75.19l21.37-8.545c.837-.334 1.687-1.133 1.687-2.384C55.425 1.99 53.944 2 53.044 2h-21.37C15.134 2 1.667 15.46 1.667 32c0 16.543 13.467 30 30.007 30 16.538 0 29.918-13.458 29.993-30 .01-2.5-1.24-2.5-1.24-2.5h-1.25" />
    </svg>
    <p style={{marginTop:'26px'}}>资料认证中，请耐心等待</p>
  </div>

);

//全局隐藏 Toast.hide()
const offline = ( () => {
  Toast.info(customIcon(), 20,function(){ alert('弹窗已自动关闭') });
} )

const handleClick = ((setp_code : string) => {
  if(setp_code === 'gps_location'){
    console.log('gps_location')
  }else{
    console.log('alse')
  }
})

const InfoList: React.FunctionComponent<InfoListProps> = ({
  stepList,
  creditTips,
  submitText,
  submitStatus,
  agreementInfo,
}) => {
  return (
    <div className={styles.infoList}>
      <p className={styles.creditTips}>{creditTips}</p>
      <div className={styles.list}>
        {stepList.toJS().map((item: any, key: any) => {
          if(item.step_status === 0){
            return (
              <Flex key={key} className={styles.item} onClick={ () => handleClick(item.setp_code) } >
                <Item className={styles.left}>
                  <img src={item.step_icon} alt="" />
                  {item.step_title}
                </Item>
                <span className={item.step_status === 0 ? styles.stepUnDid : styles.stepDid}>{item.step_info}</span>
              </Flex>
            )
          }else{
            return (
              <Flex key={key} className={styles.item} >
                <Item className={styles.left}>
                  <img src={item.step_icon} alt="" />
                  {item.step_title}
                </Item>
                <span className={item.step_status === 0 ? styles.stepUnDid : styles.stepDid}>{item.step_info}</span>
              </Flex>
            )
          }
        })}
      </div>
      <Flex className={styles.agree}>
        <Switch
          className={styles.agreeText}
          checked={true}
          onChange={() => {
            return null
          }}
        >
          我已阅读并同意
        </Switch>
        <Link to={agreementInfo.agreementUrl} className={styles.agreement_name}>
          {agreementInfo.agreementName}
        </Link>
      </Flex>
      <Button
        disabled={submitStatus === 1}
        className={styles.submitText}
        type="warning"
        onClick={ () =>  offline() }
      >
        {submitText}
      </Button>
    </div>
  )
}

export default InfoList
